import React from "react";
import axios from "axios";
import Nav from "../komponen/Nav";
import {  useForm, SubmitHandler } from "react-hook-form";
import Endpoint from "../config/Endpoint";

export default function  AddJenisProduk() {

    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => {
        axios.post(Endpoint.BASE_URL + `jenis_produk`,  data )
            .then(res => {
                if (res.data.status == false){

                }
            })
    };

    return(
        <div>
            <Nav/>
            <div className="container">
                <div className="container-fluid">

                    <h2>Tambah Jenis Produk</h2>

                    <br/>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-floating mb-3">
                            <input type="text" {...register("nama",{required:true})} className="form-control" name="nama" id="nama" placeholder="Masukkan nama jenis"/>
                            <label htmlFor="nama">Nama jenis produk</label>
                        </div>
                        {errors.nama && <span>Nama harus diisi</span>}
                        <div className="col-md-12 form-group">
                            <input type="password" {...register("keterangan",{required:true})} name="keterangan" placeholder="Masukkan keterangan" className="form-control"/>
                        </div>
                        {errors.password && <span>Keterangan harus diisi</span>}
                        <br/>
                        <center>
                            <div className="col-md-12 form-group">
                                <button className="btn btn-primary" type="submit">Simpan</button>
                            </div>
                        </center>
                    </form>
                </div>
            </div>
        </div>
    )
}
