import React, {useEffect, useState} from "react";
import Nav from "../komponen/Nav";
import Endpoint from "../config/Endpoint";
import axios from "axios";
import {Link} from "react-router-dom";
import Loading from "../komponen/Loading";

export default function Produk(){

    const [payload,setPayload]=useState([])
    const [load,setLoad]=useState(false)
    const getPayload = () => {
        setLoad(true)
        axios.get(Endpoint.BASE_URL + `produk`)
            .then(res => {
                console.log(res.data.payload)
                setPayload(res.data.payload)
                setLoad(false)
            })
    }
    useEffect(() => {
        getPayload()
    },[])

    if(load){
        return (
            <div>
                <Loading/>
            </div>
        )
    }else{
        return(
            <div>
                <Nav/>

                <div className="container">
                    <div className="container-fluid">
                        <h2>Produk</h2>
                        <br/>

                        <Link to="/add_produk" className="btn btn-primary">Tambah</Link>
                        <br/>
                        <br/>


                        <table className="table table-bordered table-hover">
                            <thead>
                            <tr>
                                <th>Gambar</th>
                                <th>Nama</th>
                                <th>Jenis</th>
                                <th>Satuan</th>
                                <th>Harga Beli</th>

                            </tr>
                            </thead>
                            <tbody>
                            {payload.map(x => (
                                <tr key={x.id}>
                                    <td><img width="60" height="60" src={Endpoint.GAMBAR_URL + x.gambar}/></td>
                                    <td>{x.nama}</td>
                                    <td>{x.jenis}</td>
                                    <td>{x.satu}</td>
                                    <td style={{"text-align":"right"}}>{'Rp. ' + new Intl.NumberFormat('en-IN',{maximumSignificantDigits:3}).format(x.hbeli) }</td>

                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }


}
